children.afterDataLoad:
function(source,model,data,result,inputData) { if(result && result.signedURL) {
   setValueAtPath(model,'displaySecureContentContainer', true); 
   setValueAtPath(data,'signedUrl', result.signedURL); 
   var isFromAddImageMetaData = source.$azUtility.getDataFormUIProcess(data,"fromAddImageMetaData");
   if(isFromAddImageMetaData){
     source.$azUtility.setDataForUIProcess(data,"customThumbnail",result.signedURL);
   }
}}

children.checkVisibility:
function(source,model,data) { if(data && data.origin == 'kloojj') {
  if(data.fileName){
     setValueAtPath(data,'fileName', data.fileName); 
  }else if(data.sourceUrl){
    var sourceUrlStr = data.sourceUrl;
    var splittedSourceUrl = sourceUrlStr.split('/');
    var fileName = splittedSourceUrl[splittedSourceUrl.length-1];  
    setValueAtPath(data,'fileName', fileName); 
  }
  return true;
} else {
  setValueAtPath(model,'displaySecureContentContainer', true); 
  return false;
}
}

