generatethumbfornewkloojj.afterProcessing:
function($event,source,model,data,result,resposeData) { if(data.id){
setTimeout(function(){
   var eventName = "thumbnailUpdatedForId" + data.id;
   source.$root.emitEvent(eventName, result); 
   source.$root.emitEvent("updateSeekBarThumbnail");
},1000);
}
}

insertkloojj.afterProcessing:
function($event,source,model,data,result,resposeData) { source.$azUtility.saveContent($event,source,model,data,result);
source.$azUtility.checkPlayerState($event,source,model,data,result);
if (model.responseData && model.responseData.sourceMode && model.responseData.sourceMode.type == "iframe") {
  var succMsg = (source.uiprops.eventHandlers && source.uiprops.eventHandlers.onclick.successInfo) ? source.uiprops.eventHandlers.onclick.successInfo.message : "Content Added successfully";
  var iframeData = { "extData": { "status": "Success", "message": succMsg, "content": result, "event": "addContent" } };
  window.parent.postMessage(iframeData, model.responseData.sourceMode.domain);
}
source.$root.hideModal();}

initiates3upload.afterProcessing:
function($event,source,model,data,result,resposeData) { var insertedKloojjData = source.$azUtility.getDataFormUIProcess(data,'insertedKloojjMetaData');
result.updatedKloojj.user_channel_Name = insertedKloojjData.user_channel_Name ? insertedKloojjData.user_channel_Name : "";
result.updatedKloojj.userPhotoUrl = insertedKloojjData.userPhotoUrl ? insertedKloojjData.userPhotoUrl : result.updatedKloojj.userPhotoUrl;
var eventName = "contentChangedForId" + data.id;
source.$root.emitEvent(eventName, result.updatedKloojj);
if(data.uiProcessObject && data.uiProcessObject.insertedKloojjMetaData) {
	delete data.uiProcessObject.insertedKloojjMetaData;
}

}

generatethumbforupdatedkloojj.afterProcessing:
function($event,source,model,data,result,resposeData) { if(data.id){
   var eventName = "thumbnailUpdatedForId" + data.id;
   source.$root.emitEvent(eventName, result); 
   source.$root.emitEvent("updateSeekBarThumbnail");
}}

oncreated.beforeProcessing:
function($event,source,model,data,inputData) { var sourceMode = source.$store.getters.getContentData({"pageId": source.$azUtility.getPageResourceId("addContent"), "dataPath": "responseData.sourceMode"});
if(sourceMode && sourceMode.type == "iframe"){
  source.$root.hideSpinner();
  var entityData = model.dataModels.kloojj;
  var iframeData = sourceMode.data;
  var contentData = {};
  for (const property in entityData) {
    if (iframeData[property]) {
      contentData[property] = iframeData[property];
    } else {
      contentData[property] = null;
    }
  }
  contentData = source.$azUtility.defaultContentValues(contentData);
  setValueAtPath(model, source.uiprops.dataPath, contentData);
}}

